package com.jivosite.sdk.apiimport androidx.lifecycle.LiveDataimport com.jivosite.sdk.model.pojo.config.Configimport com.jivosite.sdk.model.pojo.response.Responseimport com.jivosite.sdk.network.response.ApiResponseimport com.jivosite.sdk.network.retrofit.ChangeUrlInterceptor.Companion.SDKimport com.jivosite.sdk.network.retrofit.ChangeUrlInterceptor.Companion.URLimport retrofit2.http.GETimport retrofit2.http.Headersimport retrofit2.http.Path/** * Created on 4/28/21. * * @author Alexander Tavtorkin (tavtorkin@jivosite.com) */interface SdkApi {    @GET("config/{widgetId}")    @Headers("$URL:$SDK")    fun getConfig(            @Path("widgetId") widgetId: String,    ): LiveData<ApiResponse<Config>>}