package com.jivosite.sdk.apiimport androidx.lifecycle.LiveDataimport com.jivosite.sdk.model.pojo.history.HistoryResultimport com.jivosite.sdk.model.pojo.config.Configimport com.jivosite.sdk.network.response.ApiResponseimport com.jivosite.sdk.network.retrofit.ChangeUrlInterceptor.Companion.NODEimport com.jivosite.sdk.network.retrofit.ChangeUrlInterceptor.Companion.SDKimport com.jivosite.sdk.network.retrofit.ChangeUrlInterceptor.Companion.URLimport retrofit2.http.GETimport retrofit2.http.Headerimport retrofit2.http.Headersimport retrofit2.http.Path/** * Created on 4/28/21. * * @author Alexander Tavtorkin (tavtorkin@jivosite.com) */interface SdkApi {    @GET("config/{widgetId}")    @Headers("$URL:$SDK")    fun getConfig(@Path("widgetId") widgetId: String): LiveData<ApiResponse<Config>>    @GET("client/{siteId}/{widgetId}/messages?")    @Headers("$URL:$NODE")    fun getHistory(        @Header("x-jv-client-id") clientId: String,        @Path("siteId") siteId: Long,        @Path("widgetId") widgetId: String,    ): LiveData<ApiResponse<HistoryResult>>}