package com.jivosite.sdk.model.repository.mediaimport androidx.lifecycle.LiveDataimport com.jivosite.sdk.api.MediaApiimport com.jivosite.sdk.network.resource.NetworkResourceimport com.jivosite.sdk.network.resource.Resourceimport com.jivosite.sdk.support.async.Schedulersimport javax.inject.Inject/** * Created on 12.10.2021. * * @author Alexander Tavtorkin (av.tavtorkin@gmail.com) */class MediaRepositoryImpl @Inject constructor(    val api: MediaApi,    val schedulers: Schedulers) : MediaRepository {    override fun createRequest(path: String): LiveData<Resource<Unit>> {        return NetworkResource.Builder<Unit, Unit>(schedulers)            .createCall { api.checkMedia(path) }            .handleResponse { }            .build()            .asLiveData()    }}