package com.jivosite.sdk.network.retrofitimport com.jivosite.sdk.BuildConfigimport okhttp3.Interceptorimport okhttp3.OkHttpimport okhttp3.Response/** * Created on 31.01.2022. * * @author Alexander Tavtorkin (tavtorkin@jivosite.com) */class UserAgentInterceptor : Interceptor {    companion object {        const val USER_AGENT_HEADER = "User-Agent"    }    override fun intercept(chain: Interceptor.Chain): Response {        val requestBuilder = chain.request()            .newBuilder()            .removeHeader(USER_AGENT_HEADER)        "JivoSDK Android ${BuildConfig.VERSION_NAME} (okhttp ${OkHttp.VERSION})".let {            requestBuilder.addHeader(USER_AGENT_HEADER, it)        }        return chain.proceed(requestBuilder.build())    }}