package com.jivosite.sdk.network.retrofit.error/** * Created on 18.11.2020. * * @author Alexander Tavtorkin (av.tavtorkin@gmail.com) */class JivoApiException(val errors: Map<String, String>) : RuntimeException() {    override fun getLocalizedMessage(): String {        return StringBuilder(errors.toString()).run {            if (length > 2) subSequence(1, length - 2) else this        }.toString()    }}