package com.jivosite.sdk.network.retrofit.error/** * Created on 18.11.2020. * * @author Alexander Tavtorkin (av.tavtorkin@gmail.com) */class JivoApiException(val errors: Map<String, String>) : RuntimeException() {    override fun getLocalizedMessage(): String {        return StringBuilder(mapOf<String, String>().toString()).apply {            deleteRange(0, 2)            if (length > 0) deleteAt(length - 1)        }.toString()    }}