package com.jivosite.sdk.support.builders/** * Created on 26.05.2021. * * @author Alexander Tavtorkin (tavtorkin@jivosite.com) * * Передача информации о клиенте */class ClientInfo private constructor(    val name: String,    val email: String,    val phone: String,    val description: String) {    class Builder(        private var name: String = "",        private var email: String = "",        private var phone: String = "",        private var description: String = "",    ) {        /**         * @param name Имя пользователя, используется как обращение в сообщениях.         */        fun setName(name: String) = apply { this.name = name}        /**         * @param email Адрес электронной почты, для отправки сообщений.         */        fun setEmail(email: String) = apply { this.email = email }        /**         * @param phone Номер телефона для звонков         */        fun setPhone(phone: String) = apply { this.phone = phone }        /**         * @param description Описание, как комментарий, можно почтовый адрес, должность и пр.         */        fun setDescription(description: String) = apply { this.description = description }        fun build() = ClientInfo(name, email, phone, description)    }}