package com.jivosite.sdk.support.buildersimport androidx.annotation.ColorResimport androidx.annotation.DrawableResimport androidx.annotation.StringResimport com.jivosite.sdk.R/** * Created on 07.06.2021. * * @author Alexander Tavtorkin (av.tavtorkin@gmail.com) */class Config private constructor(    val background: Int?,    val logo: Int?,    val isLogoVisible: Boolean,    val title: Int?,    val subtitle: Int?,    val titleTextColor: Int?,    val subtitleTextColor: Int?,    val subtitleTextColorAlpha: Float?,    val navigationIconTint: Int?,    var welcomeMessage: Int?,    var outgoingMessageColor: Color) {    class Builder {        @DrawableRes private var background: Int? = null        @DrawableRes private var logo: Int? = null        private var isLogoVisible: Boolean = true        @ColorRes private var navigationIconTint: Int? = null        @StringRes private var title: Int? = null        @StringRes private var subtitle: Int? = null        @ColorRes private var titleTextColor: Int? = null        @ColorRes private var subtitleTextColor: Int? = null        private var subtitleTextColorAlpha: Float? = null        @StringRes private var welcomeMessage: Int? = null        private var color: Color = Color.GREEN        fun setBackground(@DrawableRes backgroundResId: Int) = apply { this.background = backgroundResId }        fun setLogo(@DrawableRes logoResId: Int) = apply { this.logo = logoResId }        fun hideLogo() = apply { this.isLogoVisible = false }        fun setTitle(@StringRes titleResId: Int) = apply { this.title = titleResId }        fun setSubtitle(@StringRes subtitleResId: Int) = apply { this.subtitle = subtitleResId }        fun setTitleTextColor(@ColorRes colorResId: Int) = apply {            this.titleTextColor = colorResId            this.navigationIconTint = colorResId        }        fun setSubtitleTextColor(@ColorRes colorResId: Int) = apply {            this.subtitleTextColor = colorResId        }        fun setSubtitleTextColorAlpha(alpha: Float) = apply {            this.subtitleTextColorAlpha = alpha        }        fun setWelcomeMessage(@StringRes welcomeMessageResId: Int) = apply { this.welcomeMessage = welcomeMessageResId }        fun setOutgoingMessageColor(color: Color) = apply { this.color = color }        fun build() = Config(            background,            logo,            isLogoVisible,            title,            subtitle,            titleTextColor,            subtitleTextColor,            subtitleTextColorAlpha,            navigationIconTint,            welcomeMessage,            color        )    }    enum class Color(@ColorRes val color: Int) {        GREEN(R.color.apple),        GREY(R.color.eastBay),        BLUE(R.color.royalBlue)    }}