package com.jivosite.sdk.ui.chat.items.message.offlineimport android.view.Viewimport android.widget.TextViewimport com.jivosite.sdk.Jivoimport com.jivosite.sdk.Rimport com.jivosite.sdk.support.dg.AdapterDelegateViewHolderimport com.jivosite.sdk.ui.chat.items.ChatEntry/** * Created on 22.02.2022. * * @author Aleksandr Tavtorkin (tavtorkin@jivosite.com) */class OfflineMessageItemViewHolder(itemView: View) : AdapterDelegateViewHolder<ChatEntry>(itemView) {    init {        val welcomeTextView = itemView.findViewById<TextView>(R.id.offline)        welcomeTextView.text = context.getString(Jivo.getConfig().offlineMessage ?: R.string.offline_message_placeholder)    }}